import React, { useState, useEffect } from 'react'
import { Tweet } from 'react-twitter-widgets'
import LazyLoad from 'react-lazy-load';


const EmbeddedTweet = () => {
  var tweets = ['1491255003268644866', '1383105268448305155', '463440424141459456']
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    console.log(isLoading)
  }, [isLoading])


  return (
    <div style={{width:'60vh', height:'auto', position:'absolute', bottom:'0', right:'0', marginBottom:'1%'}}>
      <div style={{height:'100vh', overflow:'auto'}}>
        {
          tweets.map((tweet) => (
            <div key={tweet} style={{marginBottom:'3%'}}>
              <LazyLoad height={720} onContentVisible={() => setIsLoading(false)} >
                <Tweet tweetId={tweet}/>
              </LazyLoad>
            </div>
          ))
        }
      </div>

    </div>
  )
}

export default EmbeddedTweet
