import React, { useState, useEffect } from 'react'
import PropTypes from 'prop-types';
import { Box, Grid, Typography } from '@mui/material';
import MapChart from '../MapChart/MapChart'

const MovieOverview = (props) => {
    const [toggle, setToggle] = useState(false)

    const title = props.title
    const year = props.year
    const imgURL = props.imgURL
    const releaseDate = props.releaseDate
    const runningTime = props.runningTime
    const rating = props.rating
    const ratingReason = props.ratingReason
    const genres = props.genres
    const plot = props.plot

    function handleToggle() {
        setToggle(!toggle)
    }

    return (
        <div>
            <div className='row' style={{height:'500px'}}>
                <div className='col-3'>
                    <img src={imgURL}
                        style={{display:'block', maxHeight:'500px', maxWidth:'100%', width:'auto', height:'auto'}}
                        onMouseEnter={() => setToggle(true)} /> :

                </div>
                <div className='col-3' style={{color:'#fff', backgroundColor:'grey'}}>
                    <div className='row-6'>
                    {title}
                    </div>
                </div>
                <div className='col-6' style={{color:'#fff', backgroundColor:'white'}}>

                </div>
            </div>
        </div>
    )
}

MovieOverview.propTypes = {
    title: PropTypes.string.isRequired,
    year: PropTypes.string.isRequired,
    imgURL: PropTypes.string.isRequired,
    releaseDate: PropTypes.string.isRequired,
    runningTime: PropTypes.string.isRequired,
    rating: PropTypes.string.isRequired,
    ratingReason: PropTypes.string.isRequired,
    genres: PropTypes.array.isRequired,
    plot: PropTypes.string.isRequired
}

// MovieOverview.defaultProps = {
// }

export default MovieOverview
