import React, { useState, useEffect } from 'react'
import PropTypes from 'prop-types';
import { Box, Grid, Typography } from '@mui/material';

// import EmbeddedTweet from '../EmbeddedTweet/EmbeddedTweet';
import { Tweet } from 'react-twitter-widgets'

import classNames from 'classnames';
import styles from './MovieOverview.module.css'


const MovieOverview = (props) => {
    const container = classNames(styles.container);
    const image = classNames(styles.image);
    const middle = classNames(styles.middle);
    const text = classNames(styles.text);

    const [onHover, setOnHover] = useState(false);

    const title = props.title;
    const year = props.year;
    const imgURL = props.imgURL;
    const releaseDate = props.releaseDate;
    const runningTime = props.runningTime;
    const rating = props.rating;
    const ratingReason = props.ratingReason;
    const genres = props.genres;
    const plot = props.plot;

    const tweets = ['1491255003268644866', '1383105268448305155', '463440424141459456']


    return (
        <div className={container}>
            <div className='row'>
                <div className='col-3'>
                    <div className={container}>
                    <img src={imgURL} className={image} />
                    <div className={middle} style={{color:'white'}}>
                        <div style={{textAlign:'right'}}>{releaseDate}</div>
                        <div>{title} ({year})</div>

                        <div className='row'>
                            <div className='col'>{runningTime} min</div>
                            <div className='col'>{rating}</div>
                            <div className='col'>{ratingReason}</div>
                        </div>
                        <div style={{textAlign:'left'}}>
                            <div className={text}>Genre</div>
                        </div>

                        <div className='row'>
                            {genres.map((genre) => {
                                return (
                                    <div className='col'>{genre}</div>
                                )
                            })}
                        </div>
                        <div className='row' style={{textAlign:'left', margin:'3%'}}>{plot}</div>
                    </div>
                    </div>
                </div>
                {/* <div className='col-3' style={{color:'#fff', backgroundColor:'grey'}}>
                    <div className='row-6'>

                    </div>
                </div> */}
                <div className='col-9' style={{color:'#fff', backgroundColor:'white'}}>
                    sdaf
                </div>
            </div>
        </div>
    )
}

MovieOverview.propTypes = {
    title: PropTypes.string.isRequired,
    year: PropTypes.string.isRequired,
    imgURL: PropTypes.string.isRequired,
    releaseDate: PropTypes.string.isRequired,
    runningTime: PropTypes.string.isRequired,
    rating: PropTypes.string.isRequired,
    ratingReason: PropTypes.string.isRequired,
    genres: PropTypes.array.isRequired,
    plot: PropTypes.string.isRequired
}

// MovieOverview.defaultProps = {
// }

export default MovieOverview
